Scripts.OnValueChanged:
HDH_AT_OP_OnValueChanged(self, value, userInput)
if 0 > value then
	getglobal(self:GetName().. 'TextValue'):SetText("제한 없음");
elseif 1 > value then
	getglobal(self:GetName().. 'TextValue'):SetText("0초\r\n(재사용 대기시간일 때, 표시하지 않음)");
else
	getglobal(self:GetName().. 'TextValue'):SetText(("%d:%02d 이상 표시하지 않음"):format(value/60, value%60));
end

Scripts.OnMinMaxChanged:
if min == -1 then
	getglobal(self:GetName() .. 'Low'):SetText("제한 없음");
end

